<script>$hdwt(document).ready(function(){	$hdwt('#btn_create_new_channel').click(function(){		var thispageurl = $hdwt('#this_page_url').val();		var channel_name =$hdwt('#txt_new_channel').val();						if($hdwt.trim($hdwt("#txt_new_channel").val()) != '')		{    		if($hdwt('#agree_create_channel').is(':checked'))    		{    			var channel_category = $hdwt('#channel_category').find(":selected").val();		        		$hdwt.post(thispageurl,{channel_name:channel_name,mychannel_category:channel_category}, function( data ) {        			window.location.href=thispageurl+'channel='+data;        		});        		$hdwt('.err_new_channel').hide();    			    		}    		else    		{    			$hdwt('.err_agrement').show();    		}		}		else		{			$hdwt('.err_new_channel').show();		}	});});</script><?php$get_category   = $wpdb->get_results("SELECT * FROM $category_table_name");$channel_view .= '<script src="'.$js_url.'" type="text/javascript"></script>				<link rel="stylesheet" href="'.$css_url.'" type="text/css">				<input type="hidden" value="'.$pluginurl.'" id="this_page_url"/>		        				<br>				<div id="create_new_channel" style="color:#333;">				<b><span>Create a new Channel</span></b>				<br><br>				<div style="display:inline-block; border:1px solid #ebebeb; float:left;">				<div style=" padding:20px;">				<span>Name Your Channel</span>				<br>				<input style="width:300px;" id="txt_new_channel" name="channel_name" type="text" value=""/><br>		        <span class="err_new_channel" style="display:none; color:red; font-size:12px;">You can\'t leave this empty.</span>		        <br>				<select style="width:300px;" id="channel_category" name="channel_category" onchange="javascript:changeType(this.options[this.selectedIndex].id);">';for($k = 0 ; $k < count($get_category) ; $k++){
