type  = 'video';		  break;	 }		 $hdwt.post(location.href,{new_upload_video_title:title,new_upload_video_url:url,new_video_upload_type:type,new_upload_video_thumb:thumb,new_upload_video_streamer:streamer}, function( data ) {		 window.location='<?php echo $pluginurl;?>uploads='+data;	 });			});	});</script><?php$get_settings = $wpdb->get_row("SELECT * FROM $settings_table_name");$one ='<style>';                             $one .='#video_upload_div {            					    width: 245px;            					    height: 203px;            					    background: url('.$img_folder_path.'noupload_normal.png);            					    background-repeat:no-repeat;            					    overflow: hidden;                                    margin: auto;					  }';  								$one .='#video_upload_div input {					    display: block !important;					    width: 245px !important;					    height: 203px !important;					    opacity: 0 !important;					    overflow: hidden !important;					    cursor:pointer;										}';                                       $one .='#video_upload_div:hover{ 						width: 245px;                        					    height: 203px;                        					    background: url('.$img_folder_path.'noupload_hover.png);                        					    background-repeat:no-repeat;                        					    overflow: hidden; 					}';                    					 					$one .='#dragandrophandler					{						border:2px dotted #0B85A1;						width:400px;						color:#92AAB0;											padding:10px 10px 10 10px;						margin-bottom:10px;						font-size:200%;					}					.progressBar					 {					    width: 50%;;					    height: 22px;					    border: 1px solid #ddd;					    border-radius: 5px;					    overflow: hidden;					    display:inline-block;					    margin:0px 10px 5px 5px;					    vertical-align:top;					}					.progressBar div					{					    height: 100%;					    color: #fff;					    text-align: right;					    line-height: 22px; /* same as #progressBar height if we want text middle aligned */					    width: 0;					    background-color: #0ba1b5; border-radius: 3px;					}					.statusbar					{					    border-top:1px solid #A9CCD1;					    min-height:25px;					    width:700px;					    padding:10px 10px 0px 10px;					    vertical-align:top;					}					.statusbar:nth-child(odd)					{					    background:#EBEFF0;					}					.filename					{						display:inline-block;						vertical-align:top;						width:250px;					}					.filesize					{						display:inline-block;						vertical-align:top;						color:#30693D;						width:100px;						margin-left:10px;						margin-right:5px;					}					.abort{					    background-color:#A8352F;					    -moz-border-radius:4px;					    -webkit-border-radius:4px;					    border-radius:4px;display:inline-block;					    color:#fff;					    font-family:arial;font-size:13px;font-weight:normal;					    padding:4px 15px;					    cursor:pointer;					    vertical-align:top					    }							</style>
				<script>					function sendFileToServer(formData,status)					{					    var uploadURL ="'.$pluginurl1.'"; //Upload URL					    var extraData ={}; //Extra Data.					    var jqXHR=$hdwt.ajax({					            xhr: function() {					            var xhrobj = $hdwt.ajaxSettings.xhr();					            if (xhrobj.upload) {					                    xhrobj.upload.addEventListener(\'progress\', function(event) {					                        var percent = 0;					                        var position = event.loaded || event.position;					                        var total = event.total;					                        if (event.lengthComputable) {					                            percent = Math.ceil(position / total * 100);					                        }					                        //Set progress					                        status.setProgress(percent);					                    }, false);					                }					            return xhrobj;					        },					    url: uploadURL,					    type: "POST",					    contentType:false,					    processData: false,					        cache: false,					        data: formData,					        success: function(data){                                if(data == "err")			                    {  			                             alert("Video does not support...!");			                             window.location="'.$pluginurl.'upload";			                    }					            else					            {
					                   status.setProgress(100);
					                   window.location="'.$pluginurl.'uploads="+data;					            }					        }					    });					    status.setAbort(jqXHR);					}					var rowCount=0;					function createStatusbar(obj)					{					     rowCount++;					     var row="odd";					     if(rowCount %2 ==0) row ="even";					     this.statusbar = $hdwt("<div class=\'statusbar "+row+"\'></div>");					     this.filename = $hdwt("<div class=\'filename\'></div>").appendTo(this.statusbar);					     this.size = $hdwt("<div class=\'filesize\'></div>").appendTo(this.statusbar);					     this.progressBar = $hdwt("<div class=\'progressBar\'><div></div></div>").appendTo(this.statusbar);					     this.abort = $hdwt("<div class=\'abort\'>Abort</div>").appendTo(this.statusbar);					     obj.after(this.statusbar);					    this.setFileNameSize = function(name,size)					    {					        var sizeStr="";					        var sizeKB = size/1024;					        if(parseInt(sizeKB) > 1024)					        {					            var sizeMB = sizeKB/1024;					            sizeStr = sizeMB.toFixed(2)+" MB";					        }					        else					        {					            sizeStr = sizeKB.toFixed(2)+" KB";					        }					        this.filename.html(name);					        this.size.html(sizeStr);					    }					    this.setProgress = function(progress)					    {					        var progressBarWidth =progress*this.progressBar.width()/ 100;					        this.progressBar.find(\'div\').animate({ width: progressBarWidth }, 10).html(progress + "% ");					        if(parseInt(progress) >= 100)					        {					            this.abort.hide();					        }					    }					    this.setAbort = function(jqxhr)					    {					        var sb = this.statusbar;					        this.abort.click(function()					        {					            jqxhr.abort();					            sb.hide();					        });					    }					}					function handleFileUpload(files,obj)					{					   for (var i = 0; i < files.length; i++)					   {					        var fd = new FormData();					        fd.append(\'video_upload_file\', files[i]);					        var status = new createStatusbar(obj); //Using this we can set progress.					        status.setFileNameSize(files[i].name,files[i].size);					        sendFileToServer(fd,status);					   }					}					$hdwt(document).ready(function()					{								$hdwt(\'#dragandrophandler\').change(function(e){									 var files = this.files;								     //We need to send dropped files to Server								     handleFileUpload(files,obj);					                 $hdwt(\'#video_upload_div\').hide();					                 $hdwt(\'#pleasewait_div\').show();					             										});					var obj = $hdwt("#dragandrophandler");					obj.on(\'dragenter\', function (e)					{					    e.stopPropagation();					    e.preventDefault();					    $hdwt(this).css(\'border\', \'2px solid #0B85A1\');					});					obj.on(\'dragover\', function (e)					{					     e.stopPropagation();					     e.preventDefault();					});';					$one .='$hdwt(document).on(\'dragenter\', function (e)					{					    e.stopPropagation();					    e.preventDefault();					});					$hdwt(document).on(\'dragover\', function (e)					{					  e.stopPropagation();					  e.preventDefault();					  obj.css(\'border\', \'2px dotted #0B85A1\');					});					$hdwt(document).on(\'drop\', function (e)					{					    e.stopPropagation();					    e.preventDefault();					});					});				</script>';$channel_view .='<div id="another_way" style="width: 100%; margin-top:25px; color: #333;">		<div style="width: 30%; float: left; border-right: 1px solid #E0E0E0; padding: 20px;">'; $channel_view .='<div id="click_upload" class="my_video_type">    
