			<br>';}			if($get_settings->urlvideo == "yes"){    $channel_view .='<div id="click_url" class="my_video_type">				<div style="float: left;"><img src="'.$img_folder_path.'upload_url.ico" style="width: 25px; height: 25px;" /></div>				<div style="padding-left: 10px; float: left;">Video URL</div>				<div style="clear: both"></div>			</div>';}			$channel_view .='</div>		<div style=" float: left; width:60%;">            <div id="upload" class="video_type">'.$one.'<div id="video_upload_div" class="upload"><input id="dragandrophandler" type="file" name="video_upload_file"/></div>					            <div style="display:none; text-align:center;" id="pleasewait_div"><img src="'.$img_folder_path.'loading.gif"/></div>					            </div>';                        if($get_settings->youtube == "yes")            {			$channel_view .=' 
