<?php$get_tags = @$wpdb->get_row($wpdb->prepare("SELECT * FROM $video_table_name WHERE video_id=%d",$_GET['v']));$all_tags = explode(',', $get_tags->video_tags);$video_query .= 'video_title LIKE "%'.$get_tags->video_title.'%" OR video_tags LIKE "%'.$get_tags->video_title.'%" ';for($k=1; $k < count($all_tags); $k++){	//if($k!=1){		$video_query .= ' OR ';	//}	$video_query .= 'video_title LIKE "%'.$all_tags[$k].'%" OR video_tags LIKE "%'.$all_tags[$k].'%" ';}$watch_related_video = @$wpdb->get_results("SELECT * FROM $video_table_name WHERE ".$video_query." LIMIT 0,".$hdwtube_limit);$watch_palyer .='<div style="float:left; clear: left; margin-top:10px;background:white; font-family: arial,sans-serif; ">';for($i=0;$i<count($watch_related_video);$i++){    if($watch_related_video [$i]->video_id != intval($_GET['v']))    {    $video_url      = $hdwtube_url.'v='.$watch_related_video [$i]->video_id;    $channel_url    = $hdwtube_url.'channel='.$watch_related_video[$i]->channel_id;        $get_channel_info = @$wpdb->get_row("SELECT * FROM $channel_table_name WHERE channel_id=".$watch_related_video[$i]->channel_id);            $n = date('Y-m-d H:i:s');    $video_upload_time = date_diff(    date_create($watch_related_video[$i]->video_upload_time),    date_create($n));        $up_ago = "";    if ($video_upload_time->y > 0) {        $up_ago = $video_upload_time->y . " years ago";    } else    if ($video_upload_time->m > 0) {        $up_ago = $video_upload_time->m . " months ago";    } else    if ($video_upload_time->d > 0) {        $up_ago = $video_upload_time->d . " days ago";    } else    if ($video_upload_time->h > 0) {        $up_ago = $video_upload_time->h . " hours ago";    } else    if ($video_upload_time->m > 0) {        $up_ago = $video_upload_time->m . " mins ago";    } else    if ($video_upload_time->s > 0) {        $up_ago = "Just now";    }            $watch_palyer .='<div id="widget_related_v_lst"  style="margin-bottom:15px;">';    $watch_palyer .='<div class="wl_video_property" id="wl_video_property_'.$watch_related_video [$i]->video_id.'" style="float:left; margin-right:5px; cursor:pointer; background-image: url('.$watch_related_video [$i]->video_thumpnails.');width: 120px; height:68px; background-size: 120px 90px; background-position: 0px -12px; position:relative; overflow: hidden;"   onclick="location.href=\''.$video_url.'\'">';            $watch_palyer .='<div title="watch later" style="display:none;  height: 15px; " id="wl_video_'.$watch_related_video [$i]->video_id.'" class="watch_later_property"><img src="'.$img_folder_path.'watch_later.png" style="width:15px; height:15px;"/></div>';            $watch_palyer .='     </div>';    $watch_palyer .='<div id="widget_related_v_lst_des" style="float:left; width: calc(100% - 125px); word-wrap: break-word; overflow: hidden;">';        $watch_palyer .='<div class="y_channel_title_h1" style="line-height: 1.2; cursor:pointer; font-size:13px;  overflow:hidden; color: #333; font-weight:bold;" onclick="location.href=\''.$video_url.'\'" >'.$watch_related_video [$i]->video_title.'</div>';    $watch_palyer .='<div style="color:#999; font-size: 11px;   overflow:hidden;"><div style="display: inline-block;">by </div><div style="cursor:pointer; padding-left:3px; font-size:9px; font-weight:bold; display: inline-block;" class="y_channel_title" onclick="location.href=\''.$channel_url.'\'">'.$get_channel_info->channel_name.'</div></div>';    $watch_palyer .='<div style="color:#999; font-size: 11px; line-height: 1.2; overflow:hidden;">'.$watch_related_video [$i]->video_view.' views - '.$up_ago.'</div>';       $watch_palyer .='</div>';    $watch_palyer .='<div style="clear:both;"></div>';    $watch_palyer .='</div>';    }}$watch_palyer .='</div>';echo $watch_palyer;?>