<script>function popuppppp(div, handle){	 var Popup = DYN_WEB.Popup;        Popup.setup( { id:div, handleId:handle, center:true } );        var p = Popup.getInstance(div);    // show once initialized at center    p.on_init = p.show();    blanket_size();}function blanket_size() {	 var el = document.getElementById('blanket');	    if ( el.style.display == 'none' ) {	el.style.display = 'block';              if (typeof window.innerWidth != 'undefined') {                  viewportheight = window.innerHeight;          } else {                  viewportheight = document.documentElement.clientHeight;          }          if ((viewportheight > document.body.parentNode.scrollHeight) && (viewportheight > document.body.parentNode.clientHeight)) {                  blanket_height = viewportheight;          } else {                  if (document.body.parentNode.clientHeight > document.body.parentNode.scrollHeight) {                          blanket_height = document.body.parentNode.clientHeight;                  } else {                          blanket_height = document.body.parentNode.scrollHeight;                  }          }                    el.style.height = blanket_height + 'px';	    }	    else {el.style.display = 'none';	    }}</script><script>$hdwt(document).ready(function(){		$hdwt('.remove_videofrom_watchlist').click(function(){		var deleteid = this.id;		$hdwt.post(location.href,{removewatchlistvideo:deleteid}, function( data ) {        	location.reload();		});		return false;			});    $hdwt('#btn_select_add_video').click(function(){        var videoid = '';        if($hdwt('#ylcone_playlist_addvideo_url_content').css('display') != 'block')        {                videoid = $hdwt('#sel_watch_later_id').val();                var getid = (videoid).split('video_id_')[1];                $hdwt.post(location.href,{watchlater:getid}, function( data ) {                	location.reload();                	        		});  